  sayHi : function(){
    console.log("Hi " + this.name)
  },
  setName : function(name){
      this.name = name;
  }
};

const myInfo = {
  name : "Baik",
  msg : "Hi"
};

Object.setPrototypeOf(myInfo, infoObj);

console.log(myInfo.sayHi());
//Hi Baik

myInfo.setName("Baek");

