//allTask 객체에 toDoObj를 추가하는 방법 또한 있다.

console.log("task 1~5 추가");
allTask.add("task 1");
allTask.add("task 2");
allTask.add("task 3");
allTask.add("task 4");
allTask.add("task 5");
allTask.show();
console.log("\n2,3 제거");
allTask.del(1);
